import React from 'react'
import { Link } from 'react-router-dom';

import style from "./PlanetCard.module.css"
import { AiOutlineHeart } from 'react-icons/ai';
import { AiFillHeart } from 'react-icons/ai';


export const PlanetCard = ({id, uid, favorite, planet, favoriteFuntion}) => {
  const {name, population, diameter, terrain} = planet;

  return (
    <div className={`${style.card} card g-col-12 g-col-sm-6 g-col-lg-3`}>
      <img src={`https://starwars-visualguide.com/assets/img/planets/${uid}.jpg`} className={`${style.cardImgTop} card-img-top`} />
      
      <div className={`${style.cardBody} card-body`}>
        <h5 className={`${style.cardTitle} card-title`}>{name}</h5>
        
        <div className={`${style.cardText} card-text`}>
          <p><b>Population:</b> {population}</p>
          <p><b>Diameter:</b> {diameter} km.</p>
          <p><b>Terrain:</b> {terrain}</p>
        </div>
        
        <div className={`${style.containerButtons} container-Buttons`}>
          <Link to={`/planet/${id}`} className="btn btn-dark">View more!</Link>
          {
            favorite
            ?<AiFillHeart className={style.iconCard} onClick={()=>favoriteFuntion(id)} />
            :<AiOutlineHeart className={style.iconCard} onClick={()=>favoriteFuntion(id)} />
          }
        </div>
        
      </div>
    </div>
  )
}
