import React, { useEffect, useState } from 'react'

import { CharacterCard } from '../CharacterCard/CharacterCard'
import { characterData } from '../data'

import './Character.css'

export const CharacterList = () => {
  const [characters, setCharacter]=useState([]);

  useEffect(()=>{
    setCharacter(characterData.map((character)=>{
      character.favorites = false;
      return character;
    }));
  },[])

  const switchFavorites = (id) =>{
    const updateCharacters = characters.map(item=>{
      if(item.id === id){
        item.favorite = !item.favorite;
      }
      return item;
    });
    setCharacter(updateCharacters);
  }

  return (
    <div className='mb-3'>
      <h2 className='pricipal-Title'>Character List</h2>
      <div className='grid gap-3'>
        {characters.map(item =>
          <CharacterCard
            id={item.id}
            Img={item.image}
            Name={item.name}
            Gender={item.gender}
            Height={item.height}
            Mass={item.mass}
            favorite={item.favorite}
            favoriteFuntion={switchFavorites}
          />
        )}
      </div>
    </div>
  )
}
