import { createContext, useContext, useEffect, useMemo, useState } from "react";

import {characterData} from '../Components/data'

const AppContext = createContext();

export const AppProvider = ({children}) => {
  const [characters, setCharacter]=useState([]);

  useEffect(()=>{
    setCharacter(characterData.map(character=>({favorite: false, ...character})));
  },[])

  const switchFavorites = (id) =>{
    const updateCharacters = characters.map(item=>{
      if(item.id === id){
        item.favorite = !item.favorite;
      }
      return item;
    });
    setCharacter(updateCharacters);
  }



  const store = useMemo(()=>{
    return {characters}
  },[characters]);

  const actions = {
    switchFavorites
  }

  return(
    <AppContext.Provider value={{ store, actions }}>
      {children}
    </AppContext.Provider>
  );
}

export const useAppContext = () => useContext(AppContext);
