import React from 'react'

import './CharacterCard.css'
import { AiOutlineHeart } from 'react-icons/ai';
import { AiFillHeart } from 'react-icons/ai';


export const CharacterCard = ({character, favoriteFuntion}) => {
  const {id, image, name, gender, height, mass, favorite} = character;

  return (
    <div className="card g-col-12 g-col-sm-6 g-col-lg-3">
      <img src={image} class="card-img-top" />
      
      <div className="card-body">
        <h5 className="card-title">{name}</h5>
        
        <div className="card-text">
          <p><b>Gender:</b> {gender}</p>
          <p><b>Height:</b> {height} cm.</p>
          <p><b>Mass:</b> {mass} kg.</p>
        </div>
        
        <div className='container-Buttons'>
          <a href="#" className="btn btn-dark">View more!</a>
          {favorite
            ?<AiFillHeart className='iconCard' onClick={()=>favoriteFuntion(id)} />
            :<AiOutlineHeart className='iconCard' onClick={()=>favoriteFuntion(id)} />}
        </div>
        
      </div>
    </div>
  )
}
