import React from 'react'
import { Link } from 'react-router-dom';

import  style from './CharacterCard.module.css'
import { AiOutlineHeart } from 'react-icons/ai';
import { AiFillHeart } from 'react-icons/ai';

// const peopleImage = require.context('../../Images', true);
// peopleImage(`./Character-${id}.jpg`)

// `../../src/Images/Character-${id}.jpg`


export const CharacterCard = ({uid, id, favorite, elementProp, favoriteFuntion}) => {
  const {
    name,
    gender,
    height,
    mass,
    population,
    diameter,
    terrain,
    model,
    max_atmosphering_speed,
    crew,
    cargo_capacity
  } = elementProp;
  

  return (
    <div className={`${style.card} card g-col-12 g-col-sm-6 g-col-lg-3`}>
      <img src={`https://starwars-visualguide.com/assets/img/characters/${uid}.jpg`} className={`${style.cardImgTop} card-img-top`} />
      
      <div className={`${style.cardBody} card-body`}>
        <h5 className={`${style.cardTitle} card-title`}>{name}</h5>
        
        <div className={`${style.cardText} card-text`}>
          <p><b>Gender:</b> {gender}</p>
          <p><b>Height:</b> {height} cm.</p>
          <p><b>Mass:</b> {mass} kg.</p>
        </div>
        
        <div className={`${style.containerButtons} container-Buttons`}>
          <Link to={`/character/${id}`} className="btn btn-dark">View more!</Link>
          {
            favorite
            ?<AiFillHeart className={style.iconCard} onClick={()=>favoriteFuntion(id)} />
            :<AiOutlineHeart className={style.iconCard} onClick={()=>favoriteFuntion(id)} />
          }
        </div>
        
      </div>
    </div>
  )
}
