import React, { useEffect, useState } from 'react'

import './App.css';

import {characterData} from './Components/data'
import { CharacterList } from './Components/CharacterList/CharacterList';
import { NavBar } from './Components/NavBar/NavBar';

function App() {
  const [characters, setCharacter]=useState([]);

  useEffect(()=>{
    setCharacter(characterData.map(character=>({favorite: false, ...character})));
  },[])

  const switchFavorites = (id) =>{
    const updateCharacters = characters.map(item=>{
      if(item.id === id){
        item.favorite = !item.favorite;
      }
      return item;
    });
    setCharacter(updateCharacters);
  }

  return (
    <>
      <NavBar
        charactersList={characters}
        deleteFavorite={switchFavorites} />
      <div className='container'>
        <CharacterList
          characters={characters}
          switchFavorites={switchFavorites} />
      </div>
    </>
  );
}

export default App;
