import React, { useEffect, useState } from "react";
import { useAppContext } from '../../Context/AppContext'



import "./NavBar.css"
import { LiNavBar } from "./LiNavBar";

export const NavBar = () => {
  const [favorites, setFavorites] = useState([]);
  const value = useAppContext();


  const handleFavorites = () =>{
    const favoritesList = value.store.characters.filter((item) => item.favorite === true);
    setFavorites(favoritesList);
  }
  useEffect(handleFavorites,[value.store.characters]);


  return (
    <nav className="navbar navbar-expand-sm nav-underline" data-bs-theme="dark">
      <div className="container-fluid">

        <a className="navbar-brand" href="#">STAR WARS</a>
        
        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#favoritesDropdown">
          <span className="navbar-toggler-icon"></span>
        </button>

        <div className="collapse navbar-collapse" id="favoritesDropdown">
          <ul className="navbar-nav nav-underline gap-0 ms-auto">
            
            <li className="nav-item">
              <a className="nav-link" href="#">Sing In</a>
            </li>

            <li className="nav-item dropdown">
              <a className="nav-link dropdown-toggle" href="#" role="button" data-bs-toggle="dropdown">
                Favorites
              </a>

              <ul className="dropdown-menu dropdown-menu-end">
                {
                  <LiNavBar
                    favorites={favorites}
                    deleteFavorite={value.actions.switchFavorites} />
                } 
              </ul>
            </li>

          </ul>
        </div>

      </div>
    </nav>
  );
};
