import React, { useEffect, useState } from "react";

// import { BsTrash2 } from 'react-icons/bs';

import "./NavBar.css"
import { LiNavBar } from "./LiNavBar";

export const NavBar = ({charactersList, deleteFavorite}) => {
  const [favorites, setFavorites] = useState([]);


  const handleFavorites = () =>{
    const favoritesList = charactersList.filter((item) => item.favorite === true);
    setFavorites(favoritesList);
  }
  useEffect(handleFavorites,[charactersList]);


  return (
    <nav className="navbar navbar-expand-sm nav-underline" data-bs-theme="dark">
      <div className="container-fluid">

        <a className="navbar-brand" href="#">STAR WARS</a>
        
        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#favoritesDropdown">
          <span className="navbar-toggler-icon"></span>
        </button>

        <div className="collapse navbar-collapse" id="favoritesDropdown">
          <ul className="navbar-nav nav-underline gap-0 ms-auto">
            
            <li className="nav-item">
              <a className="nav-link" href="#">Sing In</a>
            </li>

            <li className="nav-item dropdown">
              <a className="nav-link dropdown-toggle" href="#" role="button" data-bs-toggle="dropdown">
                Favorites
              </a>

              <ul className="dropdown-menu dropdown-menu-end">
                {
                  favorites.map(item=>{
                    console.log(favorites);
                    
                    return(
                      <LiNavBar
                    boolean={favorites.length>0}
                    item={item}
                    deleteFavorite={()=>deleteFavorite(item.id)} />
                    )
                  })
                  
                  // favorites.length===0
                  // ?<li className="li-Dropdown">
                  //   <a className="dropdown-item" href="#">Empty</a>
                  // </li>

                  // :favorites.map(item =>{
                  //   return (
                  //     <li className="li-Dropdown">
                  //       <a className="dropdown-item pe-1" href="#">{item.name}</a>
                  //       <BsTrash2 className="iconNavBar" onClick={()=>deleteFavorite(item.id)}/>
                  //     </li>
                  //   )
                  // })
                } 
              </ul>
            </li>

          </ul>
        </div>

      </div>
    </nav>
  );
};
