import { createContext, useContext, useEffect, useMemo, useState } from "react";

import {characterData, planetsData} from '../Components/data'

const AppContext = createContext();

export const AppProvider = ({children}) => {

  const getCharacterData = () => {
    fetch("https://www.swapi.tech/api/people")
    .then((res) => {
      if (!(res.status >= 200 && res.status < 300)) {
        throw Error(`You have an error ${res.status} in request data!`);
      }
      return res.json();
    })
    .then((body) => {
      return (body.results.map(character=>{
        fetch(character.url)
        .then((response) => {
          if (!(response.status >= 200 && response.status < 300)) {
            throw Error(`You have an error ${response.status} in request data!`);
          }
          return response.json();
        })
        .then((charaterInfo) => {
          return charaterInfo.result.properties;
        });
      }))
    })
    .then((arr)=>console.log(arr))
    .catch((err) => console.log(err));
  };
  
  getCharacterData();















  const [characters, setCharacter]=useState(characterData);
  const [planets, setPlanets]=useState([]);
  const [favorites, setFavorites] = useState([]);

  useEffect(()=>{
    setCharacter(characterData.map(character=>({favorite: false, ...character})));
  },[]);

  useEffect(()=>{
    setPlanets(planetsData.map(planet=>({favorite: false, ...planet})));
  },[])



  const switchFavoritesCharacter = (id) =>{
    const updateCharacters = characters.map(item=>{
      if(item.id === id){
        item.favorite = !item.favorite;
      }
      return item;
    });
    setCharacter(updateCharacters);
    handleFavorites();
  }

  const switchFavoritesPlanets = (id) =>{
    const updatePlanets = planets.map(item=>{
      if(item.id === id){
        item.favorite = !item.favorite;
      }
      return item;
    });
    setPlanets(updatePlanets);
    handleFavorites();
  }

  const deleteFavorite = (id) =>{
    const updateCharacters = characters.filter((item)=>{
      if(item.id === id){
        item.favorite = !item.favorite;
      }
      return item;
    });
    const updatePlanets = planets.filter((item)=>{
      if(item.id === id){
        item.favorite = !item.favorite;
      }
      return item;
    });
    setCharacter(updateCharacters);
    setPlanets(updatePlanets);
    handleFavorites();
  }

  const handleFavorites = () =>{
    const favoritesListCharacters = characters.filter((item) => item.favorite === true);
    const favoritesListPlanets = planets.filter((item) => item.favorite === true);

    const favoritesList = favoritesListCharacters.concat(favoritesListPlanets);
    setFavorites(favoritesList);
  }



  const store = useMemo(()=>{
    return {characters, planets, favorites}
  },[characters, planets]);

  const actions = {
    switchFavoritesCharacter,
    switchFavoritesPlanets,
    deleteFavorite
  }

  return(
    <AppContext.Provider value={{ store, actions }}>
      {children}
    </AppContext.Provider>
  );
}

export const useAppContext = () => useContext(AppContext);
